import React from "react";
import Test from "./Test"
import axios from "axios";

class moviesApi extends React.Component{
    constructor(props){
        super(props);
        this.title = this.movie.title;
        this.director= this.movie.director;
        this.year= this.movie.year;
        this.country= this.movie.country;
        this.posterUrl= this.movie.posterUrl;
    }


  componentDidMount() {
    this.getObject();
  }

    getObject(){
        axios
            .get(
                `https://hackathon-wild-hackoween.herokuapp.com/movies`
            )
            .then(response => response.data)
            .then(data => {
                this.setState({
                    movie: data.movies[0]
            });
            });
        }

    render() {
        const {movie} = this.state;
        return <div><Test movie={movie}/></div>
    }
}

export default moviesApi;
